create table users (
    id bigint generated by default as identity, 
    password nvarchar(255), 
    role nvarchar(255), 
    username nvarchar(255), 
    primary key (id)
);

create table comment (
    id bigint generated by default as identity, 
    avatar nvarchar(255), 
    content nvarchar(4000), 
    name nvarchar(255), 
    occupation nvarchar(255), 
    primary key (id)
);

create table orders (
    id bigint generated by default as identity, 
    address varchar(255), 
    email varchar(255), 
    name varchar(255), 
    note varchar(255), 
    phone varchar(255), 
    status varchar(255), 
    primary key (id)
);

create table products (
    id bigint generated by default as identity, 
    category nvarchar(255), 
    discount double, 
    image nvarchar(255), 
    material nvarchar(255), 
    name nvarchar(255), 
    price double, 
    size nvarchar(255), 
    type nvarchar(255), 
    primary key (id)
);



