insert into comment (name, avatar, occupation, content) values ('Nguyễn Thanh Hà', '../../assets/images/avatar1.jpg', 'Kỹ sư', 'Mình rất ấn tượng với bộ sưu tập đồ nam của shop – thiết kế trẻ trung, chất vải mát và form dáng chuẩn. Nhân viên tư vấn nhiệt tình, chắc chắn sẽ quay lại!');
insert into comment (name, avatar, occupation, content) values ('Nguyễn Huỳnh Đức', '../../assets/images/avatar2.jpg', 'Giáo viên', 'Shop có nhiều mẫu áo cực kỳ đẹp và sang, giá cả hợp lý. Mình rất thích không gian cửa hàng, rộng rãi và bày trí bắt mắt. Rất đáng để ghé thăm mỗi khi cần mua đồ!');
insert into comment (name, avatar, occupation, content) values ('Hàn Minh Hoà', '../../assets/images/avatar3.jpg', 'Sinh viên', 'Đồ trẻ em ở shop rất dễ thương, chất liệu mềm mại, an toàn cho bé. Mình mua cho con 2 bộ mà bé mặc rất thoải mái. Sẽ giới thiệu cho các mẹ bỉm khác!');
